n = int(input())  # 병사 수
array = list(map(int, input().split()))  # n명의 병사 전투력
array.reverse()  # 뒤집어 가장 긴 증가 부분 수열 문제로 변환
d = [1] * n

# 가장 긴 증가 부분 수열(LIS) 알고리즘 수행
for i in range(1, n):
  for j in range(i):
    # 앞에 있는 원소가 마지막 원소보다 작다면 갱신
    if array[j] < array[i]:
      d[i] = max(d[i], d[j] + 1)  
# 열외해야하는 최소 병사수
print(n - max(d))
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  